import React, { useState, useEffect } from 'react'
import { Link, useHistory, useLocation } from 'react-router-dom'
import { Form, Button, Row, Col } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import Loader from '../Loader'
import Message from '../Message'
import FormContainer from '../FormContainer'
import { login } from '../../actions/userAction'

const LoginScreen = () => {
	//Router Hooks
	const { search } = useLocation()
	const { push } = useHistory()
	const [email, setEmail] = useState('')
	const [password, setPassword] = useState('')

	const dispatch = useDispatch()
	const userLogin = useSelector((state) => state.userLogin)
	const { loading, erro, userInfo } = userLogin
	const redirect = search ? search.split('=')[1] : '/'
	useEffect(() => {
		if (userInfo) {
      push(redirect)
		}
	}, [push,userInfo,redirect])
	const submitHandler = (e) => {
		e.prevenDefault()
    //Dispatch Login
    dispatch(login(email,password))
	}
	return (
		<FormContainer>
      <h1>Sign In</h1>
      {error && <Message variant='danger'>{error}</Message>}
      {loading && <Loader></Loader>}
			<Form onSubmit={submitHandler}>
				<Form.Group controlid='email'>
					<Form.Label>Eamil Adress</Form.Label>
					<Form.Control type='email' placeholder='Enter email' value={email} onChange={(e) => setEmail(e.target.value)}></Form.Control>
				</Form.Group>
				<Form.Group controlid='password'>
					<Form.Label>Password</Form.Label>
					<Form.Control type='email' placeholder='Enter password' value={password} onChange={(e) => setPassword(e.target.value)}></Form.Control>
				</Form.Group>
				<Form.Group>
					<Button type='submit' variant='primary'>
						Sign In
					</Button>
				</Form.Group>
			</Form>
			<Row className='py-3'>
				<Col>
					Not Registered yet ?{' '}
					<Link to={redirect ? `/register?redirect=${redirect}` : `/register`} style={{ color: '#284a6c' }}>
						Register Now
					</Link>
				</Col>
			</Row>
		</FormContainer>
	)
}

export default LoginScreen
