import React, { useState, useEffect } from 'react'
import { useHistory, useLocation } from 'react-router-dom'
import { Form, Button } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import FormContainer from '../FormContainer'
import { saveShippingAddress } from '../../actions/cartActions'
import { getUserDetails } from '../../actions/userAction'

const ShippingScreen = () => {
	//Router Hooks
	const { push } = useHistory()

	const cart = useSelector((state) => state.cart)
	const { shippingAddres } = cart
	//Sates
	const [address, setAddress] = useState(shippingAddres.address)
	const [city, setCity] = useState(shippingAddres.city)
	const [postCode, setPostCode] = useState(shippingAddres.postCode)
	const [country, setCountry] = useState(shippingAddres.country)

	//Get Name
	const dispatch = useDispatch()
	const userDetails = useSelector((state) => state.userDetails)
	const { loading, error, user } = userDetails
	useEffect(() => {
		if (!user || !user.name) {
			dispatch(getUserDetails('profile'))
		} else {
			setName(user.name)
		}
	}, [dispatch, push, user])
	const submitHandler = (e) => {
		e.preventDefault()
		dispatch(saveShippingAddress({ name, address, city, country, postCode }))
		push('/payment')
	}
	return (
		<FormContainer>
			<h1>Shipping</h1>
			<Form onSubmit={submitHandler}>
				<Form.Group>
					<Form.Label>Name</Form.Label>
					<Form.Control
						type='name'
						placeholder='Enter name'
						autoComplete='name'
						value={name}
						onChange={(e) => setName(e.target.value)}
					></Form.Control>
				</Form.Group>
				<Form.Group controlId='address'>
					<Form.Label>Adress</Form.Label>
					<Form.Control
						type='textarea'
						as='textarea'
						rows='3'
						required
						placeholder='Enter address'
						value={address}
						onChange={(e) => setAddress(e.target.value)}
					></Form.Control>
				</Form.Group>
				<Form.Group controlId='postcode'>
					<Form.Label>Postcode</Form.Label>
					<Form.Control
						type='text'
						required
						placeholder='Enter postcode'
						value={postCode}
						onChange={(e) => setPostCode(e.target.value)}
					></Form.Control>
				</Form.Group>
				<Form.Group controlId='city'>
					<Form.Label>City</Form.Label>
					<Form.Control
						type='text'
						required
						placeholder='Enter city'
						value={city}
						onChange={(e) => setCity(e.target.value)}
					></Form.Control>
				</Form.Group>
				<Form.Group controlId='country'>
					<Form.Label>Country</Form.Label>
					<Form.Control
						type='text'
						required
						placeholder='Enter country'
						value={country}
						onChange={(e) => setCountry(e.target.value)}
					></Form.Control>
				</Form.Group>
				<Button type='submit' ariant='primary'>
					Continue
				</Button>
			</Form>
		</FormContainer>
	)
}

export default ShippingScreen
